.myselect {
  position: relative;
}
.select {
  position: relative;
  width: 200px;
  height: 30px;
  line-height: 30px;
  text-align: center;  
  border: 1px solid #ccc;
}
.select::after {
  content: '';
  position: absolute;
  top:50%;
  margin-top:-4px;
  right: 0;
  height: 0;
  width: 0;
  border-left: 5px solid transparent;
  border-top: 8px solid #333;
  border-right: 5px solid transparent;
}
ul,li {
  margin: 0;
  padding: 0;
}
.option {
  position: absolute;
  top: 32px;
  width: 200px;
  /* height: 200px; */
  border: 1px solid #ccc;
  border-top:none;
}
.opts {
  width: 100%;
  height: 30px;
  line-height: 30px;
  text-align: center;
  cursor: pointer
}

.opts:hover {
  background-color: palegoldenrod
}
